import { Component, OnInit, Input, Output, EventEmitter } from '@angular/core';
import { Product }from '../../interfaces/product.interface';

@Component({
  selector: 'app-product',
  templateUrl: './product.component.html',
  styleUrls: ['./product.component.scss']
})
export class ProductComponent implements OnInit {

  @Input() product!: Product;

  @Output() addedProduct = new EventEmitter<Product>();

  ngOnInit(): void {

  }


  onAddToCart(): void {
    this.addedProduct.emit(this.product);
  }
}
<app-img *ngIf="product.images" [img]="product.image"></app-img>
<h2>{{product.title  | reverse }}</h2>
<p appHighlight>{{product.price | currency:'EUR'}}€</p>
<p appHighlight >{{product.desctiption}}</p>
<button (click)="onAddToCart()" type="button"> Add to cart</button>
